import React, { Component } from "react";
import Nav from '../components/Nav';
import ImgContainer from '../components/ImgContainer';

// By extending the React.Component class, Counter inherits functionality from it
class GamePage extends Component {
    // Setting the initial state of the Counter component
    state = {
        search: []
    };

    handleInputChange = event => {
        const name = event.target.name;
        const value = this.state.myArray.concat(event.target.value);
        this.setState({
            [name]: value
        });
    };

    // handleIncrement increases this.state.count by 1
    handleIncrement = () => {
        // We always use the setState method to update a component's state
        this.setState({ count: this.state.count + 1 });
    };

    // handleDecrement decreases this.state.count by 1
    handleDecrement = () => {
        // We always use the setState method to update a component's state
        this.setState({ count: this.state.count - 1 });
    };



    thisIsAnEvent = event => {

        const name = event.target.name;
        const value = this.state.myArray.push(event.target.value);

        this.setState({
            [name]: this.state.myArray.push(event.target.value)
        },() => {
            console.log(this.state.search);
            console.log('-----------------------------');
        });

    }

    // The render method returns the JSX that should be rendered
    render() {
        return (

            <div>
                <Nav />
                <ImgContainer thisIsAnEvent={this.thisIsAnEvent}  />
            </div >

        );
    }
}
// <Gallery photos={this.photos} />;
export default GamePage;
