import React, { Component } from "react";
import Nav from '../components/Nav';
import ImgContainer from '../components/ImgContainer';

// By extending the React.Component class, Counter inherits functionality from it
class GamePage extends Component {
    // Setting the initial state of the Counter component

    state = {
        search: [],
        gifs: ['http://i0.wp.com/cdn-images-1.medium.com/max/1600/1*5DAQEDPv_5opTqr-y6yEag.gif', 'https://cdn.dribbble.com/users/10958/screenshots/2421804/rudolph.gif', 'https://cdn.dribbble.com/users/189524/screenshots/2375731/02-cossack_v1-dribbble.gif', 'https://cdn.dribbble.com/users/1099127/screenshots/3735598/dribbble.gif', 'https://media0.giphy.com/media/xUOwG3n2WnEIokZ9ew/source.gif', 'https://cdn.dribbble.com/users/17707/screenshots/2612185/rt-ani.gif', 'https://i.gifer.com/WyD2.gif', 'https://i.pinimg.com/originals/58/46/2c/58462c6f915dbac62df69a262a8950a2.gif', 'https://cdn.dribbble.com/users/1265589/screenshots/4443637/turtle_dribble.gif', 'https://cdn.dribbble.com/users/879235/screenshots/2438738/turtle_01.gif', 'https://data.whicdn.com/images/313044851/original.gif', 'https://cdn.dribbble.com/users/722246/screenshots/3450853/sealion_earthday.gif']

    };

    handleInputChange = event => {
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]: value
        });
    };

    // handleIncrement increases this.state.count by 1
    handleIncrement = () => {
        // We always use the setState method to update a component's state
        this.setState({ count: this.state.count + 1 });
    };

    // handleDecrement decreases this.state.count by 1
    handleDecrement = () => {
        // We always use the setState method to update a component's state
        this.setState({ count: this.state.count - 1 });
    };

   shuffle(arra1) {
        let ctr = arra1.length;
        let temp;
        let index;

        // While there are elements in the array
        while (ctr > 0) {
            // Pick a random index
            index = Math.floor(Math.random() * ctr);
            // Decrease ctr by 1
            ctr--;
            // And swap the last element with it
            temp = arra1[ctr];
            arra1[ctr] = arra1[index];
            arra1[index] = temp;
        }
        this.setState({
            gifs: arra1
        }, () => {
            console.log(this.state.search);
            console.log('-----------------------------');
        });
    }


    thisIsAnEvent = event => {

        const name = event.target.name;
        const value = this.state.search.slice();

        if (value.indexOf(event.target.src) >= 0) {
            console.log('already in arry')


        } else {
            console.log('not in arry');
            value.push(event.target.src);
        }

        this.shuffle(this.setState.gifs);


        this.setState({
            [name]: value
        }, () => {
            console.log(this.state.search);
            console.log('-----------------------------');
        });

    }

    // The render method returns the JSX that should be rendered
    render() {
        return (

            <div>
                <Nav />
                <ImgContainer thisIsAnEvent={this.thisIsAnEvent} gifs={this.state.gifs} />
            </div >

        );
    }
}
