import React, { Component } from "react";
import { Link } from "react-router-dom";

// By extending the React.Component class, Counter inherits functionality from it
class HomePage extends Component {
    // Setting the initial state of the Counter component
    state = {
        count: 0
    };

    // handleIncrement increases this.state.count by 1
    handleIncrement = () => {
        // We always use the setState method to update a component's state
        this.setState({ count: this.state.count + 1 });
    };

    // handleDecrement decreases this.state.count by 1
    handleDecrement = () => {
        // We always use the setState method to update a component's state
        this.setState({ count: this.state.count - 1 });
    };

    // The render method returns the JSX that should be rendered
    render() {
        return (

            <div>


                <div className="carousel-inner fullScreen">
                    <div className="carousel-item active slideImg">
                        <img src="https://static.giantbomb.com/uploads/original/0/2840/2495891-the-division_2013_06-10-13_008.jpg" className="d-block w-100 slideImg" alt="..." />
                    </div>
                    <div className="carousel-item slideImg">
                        <img src="https://img.youtube.com/vi/BhTkoDVgF6s/maxresdefault.jpg" className="d-block w-100 slideImg" alt="..." />
                    </div>
                    <div className="carousel-item  slideImg">
                        <img src="https://wallpapercave.com/wp/gWAwDVv.jpg" className="d-block w-100 slideImg" alt="..." />
                    </div>

                </div>
                <div className="content">

                        <h1 className="text-center mt-4 text-white"> Clicky Game</h1>


                        <h4 className="text-center mt-4 "> This will change the way you React!</h4>


                            <button type="button" className="btn btn-outline-light startBtn mt-4"><Link to="/" className={window.location.pathname === "/" ? "nav-link active" : "nav-link"}>
          Start
        </Link></button>

                </div>

            </div>

        );
    }
}

export default HomePage;
